import React from 'react'
import {Link} from 'react-router-dom'

export const Pagination = ({total, limit, url, currentPage}) => {
  const pagesCount = Math.ceil(total / limit)
  const pages = Array.from({length: pagesCount}, (_, key) => (
    <li key={key} className={`page-item ${currentPage === key + 1 ? 'active' : ''}`}>
      <Link to={`${url}?page=${key + 1}`} className="page-link">{key + 1}</Link>
    </li>
  ))
  return (
    <ul className="pagination">{pages}</ul>
  )
}