export const queryString = {
  parse(string) {
    const clearedString = string.startsWith('?') ? string.slice(1) : string
    const queriesArray = clearedString
      .split('&')
      .map(query => query.split('='))
    const queriesObject = Object.fromEntries(queriesArray)
    return queriesObject
  },
  stringify(params) {
    return Object.entries(params)
      .map(query => query.join('='))
      .join('&')
  }
}